static void test_tick_performance() {
    printf("Testing tick performance (8-tick budget)...\n");
    
    fixed_bitactor_t ba;
    fixed_bitactor_init(&ba);
    
    /* Enqueue test signals */
    fixed_signal_t sig1 = {
        .type = FIXED_SIGNAL_SEMANTICSIGNAL,
        .flags = 0,
        .timestamp = get_timestamp_ns(),
        .payload = 1
    };
    fixed_bitactor_enqueue_signal(&ba, &sig1);
    fixed_signal_t sig2 = {
        .type = FIXED_SIGNAL_N7993C4F16938425A8E5D08EDC863499FB6,
        .flags = 0,
        .timestamp = get_timestamp_ns(),
        .payload = 2
    };
    fixed_bitactor_enqueue_signal(&ba, &sig2);
    fixed_signal_t sig3 = {
        .type = FIXED_SIGNAL_N7993C4F16938425A8E5D08EDC863499FB7,
        .flags = 0,
        .timestamp = get_timestamp_ns(),
        .payload = 3
    };
    fixed_bitactor_enqueue_signal(&ba, &sig3);
    
    /* Process signals and measure performance */
    uint64_t total_ticks = 0;
    int processed = 0;
    
    while (ba.signal_head != ba.signal_tail) {
        uint64_t start = rdtsc();
        fixed_bitactor_tick(&ba);
        uint64_t elapsed = rdtsc() - start;
        
        total_ticks += elapsed;
        processed++;
        
        /* Verify tick budget */
        assert(elapsed <= FIXED_TICK_BUDGET);
    }
    
    printf("✅ Processed %d signals, avg ticks: %llu\n", processed, (unsigned long long)(total_ticks / processed));
}

static void test_ring_buffer_overflow() {
    printf("Testing ring buffer overflow protection...\n");
    
    fixed_bitactor_t ba;
    fixed_bitactor_init(&ba);
    
    fixed_signal_t sig = {
        .type = FIXED_SIGNAL_SEMANTICSIGNAL,
        .flags = 0,
        .timestamp = 0,
        .payload = 0
    };
    
    /* Fill the ring buffer */
    int enqueued = 0;
    while (fixed_bitactor_enqueue_signal(&ba, &sig)) {
        enqueued++;
        if (enqueued > FIXED_RING_SIZE) {
            break; /* Safety check */
        }
    }
    
    assert(enqueued == FIXED_RING_SIZE - 1);
    
    /* Verify overflow protection */
    bool result = fixed_bitactor_enqueue_signal(&ba, &sig);
    assert(result == false);
    
    printf("✅ Ring buffer overflow protection working (capacity: %d)\n", enqueued);
}

