void test_bitactor_tick(test_bitactor_t* ba) {
    uint64_t start_ticks = rdtsc();
    uint32_t head = ba->signal_head;
    uint32_t tail = ba->signal_tail;
    
    if (head != tail) {
        test_signal_t* sig = &ba->signal_ring[tail];
        test_handler_fn handler = ba->dispatch[sig->type];
        
        if (handler) {
            handler(sig, ba->scratch);
        }
        
        ba->signal_tail = (tail + 1) & (TEST_RING_SIZE - 1);
        ba->signal_count++;
    }
    
    uint64_t elapsed = rdtsc() - start_ticks;
    ba->tick_count += elapsed;
    
    /* Assert tick budget - disabled for benchmarks */
    #ifndef BENCHMARK_MODE
    assert(elapsed <= TEST_TICK_BUDGET);
    #endif
}

bool test_bitactor_enqueue_signal(test_bitactor_t* ba, const test_signal_t* sig) {
    uint32_t head = ba->signal_head;
    uint32_t next_head = (head + 1) & (TEST_RING_SIZE - 1);
    
    if (next_head == ba->signal_tail) {
        return false; /* Ring full */
    }
    
    ba->signal_ring[head] = *sig;
    ba->signal_head = next_head;
    return true;
}

